vertex.source:
#version 300 es

// Vertex position in object space coordinates
in vec3 vertexPosition;
// Surface normal at the vertex in object space coordinates
in vec3 vertexNormal;
// Texture coordinates at that vertex
in vec2 vertexTextureCoordinates;

// Model matrix
uniform mat4 mMatrix;
// View matrix
uniform mat4 vMatrix;
// Projection matrix
uniform mat4 pMatrix;

out vec4 color;

uniform vec3 lightPos;
uniform vec3 baseColor;
uniform vec3 lightColor;
uniform float Kd;
uniform float Ka;
uniform float Ks;
uniform float s;

// Main program for each vertex
void main() {
  vec4 vertexCamSpace = vMatrix * mMatrix * vec4(vertexPosition, 1.0);
  gl_Position = pMatrix * vertexCamSpace;

  vec3 fragN = (transpose(inverse(mMatrix)) * vec4(vertexNormal, 0.0)).xyz;;
  vec3 camPos = (inverse(vMatrix) * vec4(0.0, 0.0, 0.0, 1.0)).xyz;
  vec3 fragPos = (mMatrix * vec4(vertexPosition, 1.0)).xyz;

  vec3 n = normalize(fragN);
  vec3 l = normalize(lightPos - fragPos);
  vec3 v = normalize(camPos - fragPos);
  vec3 r = normalize(2.0 * dot(n, l) * (n - l));

  float Id = Kd * clamp(dot(n,l), 0.0, 1.0);
  float Ia = Ka;
  float Is = Ks * clamp(pow(dot(r,v),s) , 0.0, 1.0);

  color = vec4( ( ( (Ka * Ia + Kd * Id) * baseColor + (Ks * Is) ) * lightColor ), 1.0);
}

fragment.source:
#version 300 es

// For better performance less precision
precision highp float;
out vec4 fragColor;

in vec4 color;

// Main program for each fragment = pixel candidate
void main() {
  fragColor = color;
}

vertex.source:
#version 300 es

// Vertex coordinates in object space for the render quad
in vec3 vertexPosition;
// Texture coordinate for this vertex and the render quad
in vec2 vertexTextureCoordinates;

// Texture coordinate needs to be passed on to the R2T fragment shader
out vec2 fragmentTextureCoordinates;

// Main program for each vertex of the render quad
void main() {
  gl_Position = vec4(vertexPosition, 1.0);
  fragmentTextureCoordinates = vertexTextureCoordinates;
}

fragment.source:
#version 300 es

precision highp float;

// A texture sampling unit, which is bound to the render quad texture buffer
uniform sampler2D textureRendered;

// Texture coordinates coming from the vertex shader, interpolated through the rasterizer
in vec2 fragmentTextureCoordinates;
out vec4 fragColor;

// Main program for each fragment of the render quad
void main() {
  fragColor = texture(textureRendered, fragmentTextureCoordinates.st);
}
